import React, { useState } from 'react';
import './ChatWindow.css';

function ChatWindow({ onAnalysisComplete, onResumeGenerated }) {
  const [mode, setMode] = useState('analyze'); // 'analyze' or 'generate'
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const [resumePrompt, setResumePrompt] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = (event) => {
    setResumeFile(event.target.files[0]);
  };

  const handleAnalyzeSubmit = async (event) => {
    event.preventDefault();
    if (!resumeFile) {
      alert('Please upload a resume file.');
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('job_description', jobDescription);

    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Server responded with an error!');
      }

      const data = await response.json();
      onAnalysisComplete(data);
    } catch (error) {
      console.error('Error analyzing resume:', error);
      alert('Failed to analyze resume. Please check the console for details.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGenerateSubmit = async (event) => {
    event.preventDefault();
    if (!resumePrompt) {
      alert('Please enter a description for the resume you want to generate.');
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/generate-resume', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt: resumePrompt }),
      });

      if (!response.ok) {
        throw new Error('Server responded with an error!');
      }

      const data = await response.json();
      onResumeGenerated(data);
    } catch (error) {
      console.error('Error generating resume:', error);
      alert('Failed to generate resume. Please check the console for details.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-window">
      <div className="mode-selector">
        <button onClick={() => setMode('analyze')} className={mode === 'analyze' ? 'active' : ''}>Analyze Resume</button>
        <button onClick={() => setMode('generate')} className={mode === 'generate' ? 'active' : ''}>Generate Resume</button>
      </div>

      {mode === 'analyze' ? (
        <>
          <h2>Upload Your Details</h2>
          <form onSubmit={handleAnalyzeSubmit}>
            <div className="form-group">
              <label htmlFor="resume-upload">Upload Resume (PDF or DOCX)</label>
              <input
                type="file"
                id="resume-upload"
                accept=".pdf,.docx"
                onChange={handleFileChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="jd-input">Paste Job Description (Optional)</label>
              <textarea
                id="jd-input"
                rows="10"
                placeholder="Paste the job description here to get a tailored analysis..."
                value={jobDescription}
                onChange={(e) => setJobDescription(e.target.value)}
              ></textarea>
            </div>
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Analyzing...' : 'Analyze My Resume'}
            </button>
          </form>
        </>
      ) : (
        <>
          <h2>Generate a New Resume</h2>
          <form onSubmit={handleGenerateSubmit}>
            <div className="form-group">
              <label htmlFor="resume-prompt">Describe the Resume You Want</label>
              <textarea
                id="resume-prompt"
                rows="10"
                placeholder="e.g., Generate me a resume for a Java developer with 2 years of experience, skills: Java, SpringBoot, SQL..."
                value={resumePrompt}
                onChange={(e) => setResumePrompt(e.target.value)}
              ></textarea>
            </div>
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Generating...' : 'Generate Resume'}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default ChatWindow;
