import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import ChatWindow from './components/ChatWindow';
import ScoreDisplay from './components/ScoreDisplay';
import ResumeDisplay from './components/ResumeDisplay'; // Import the new component

function App() {
  const [analysisResult, setAnalysisResult] = useState(null);
  const [generatedResume, setGeneratedResume] = useState(null);

  const handleAnalysisComplete = (result) => {
    setGeneratedResume(null); // Clear generated resume when analysis is done
    setAnalysisResult(result);
  };

  const handleResumeGenerated = (resume) => {
    setAnalysisResult(null); // Clear analysis when a resume is generated
    setGeneratedResume(resume);
  };

  return (
    <div className="App">
      <Header />
      <main className="main-content">
        <div className="chat-container">
          <ChatWindow 
            onAnalysisComplete={handleAnalysisComplete} 
            onResumeGenerated={handleResumeGenerated} 
          />
        </div>
        <div className="results-container">
          {generatedResume ? (
            <ResumeDisplay resume={generatedResume} />
          ) : (
            <ScoreDisplay result={analysisResult} />
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
