import React from 'react';
import './ScoreDisplay.css';

function ScoreDisplay({ result }) {
  if (!result) {
    return (
      <div className="score-display placeholder">
        <h2>Your Analysis Results</h2>
        <p>Upload your resume and a job description to see your ATS score and get suggestions for improvement.</p>
      </div>
    );
  }

  const { score, suggestions } = result;

  return (
    <div className="score-display">
      <h2>Your Analysis Results</h2>
      <div className="score-circle">
        <span className="score-number">{score}</span>
        <span className="score-label">/ 100</span>
      </div>
      {score < 85 && <p className="score-feedback">Your resume is a good start, but let's make some improvements to beat the bots!</p>}
      {score >= 85 && <p className="score-feedback">Great job! Your resume is highly ATS-friendly.</p>}

      <h3>Suggested Changes:</h3>
      <ul className="suggestions-list">
        {suggestions.map((suggestion, index) => (
          <li key={index}>{suggestion}</li>
        ))}
      </ul>

      {result.download_path && (
         <a href={`http://localhost:5000/${result.download_path}`} download className="download-button">
            Download ATS-Friendly Resume
         </a>
      )}
    </div>
  );
}

export default ScoreDisplay;
